package security;

import java.math.BigInteger;
import java.nio.charset.StandardCharsets;
import java.security.MessageDigest;
import java.util.*;
public class mdTest {
    public static void main(String[] args) {
        try {
            final List<Double> liste = Arrays.asList(123.456, 3.14, -7.96);
            final StringBuilder sb = new StringBuilder();

            // Zahlen in StringBuilder schreiben, jede in neue Zeile
            for (Double d : liste) {
                sb.append(d.toString()).append("\n");
            }

            String text = sb.toString();
            System.out.println(text);

            // SHA-256 Hash √ºber den Text berechnen
            final MessageDigest md = MessageDigest.getInstance("SHA-256");
            final byte[] hashBytes = md.digest(text.getBytes(StandardCharsets.UTF_8));

            // Hash bytes in hexadezimale String-Darstellung konvertieren
            final BigInteger bigInteger = new BigInteger(1, hashBytes);
            String hashString = bigInteger.toString(16);

            // Falls f√ºhrende Nullen fehlen, ggf. auff√ºllen (SHA-256 hat 64 Hex-Zeichen)
            while (hashString.length() < 64) {
                hashString = "0" + hashString;
            }

            // Hash an das StringBuilder-Ergebnis anh√§ngen
            sb.append(hashString).append("\n");
            System.out.println(sb.toString());

            // Hier kann man sb.toString() in eine Datei schreiben (z.B. BufferedWriter)

        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}


